import Proxy from "./Proxy";
import { ICreateProxyBody, IPopulateProxiesBody } from "./interfaces";
export interface Proxies {
    [name: string]: Proxy;
}
export default class Toxiproxy {
    host: string;
    constructor(host: string);
    createProxy(body: ICreateProxyBody): Promise<Proxy>;
    populate(body: IPopulateProxiesBody): Promise<Proxies>;
    get(name: string): Promise<Proxy>;
    getVersion(): Promise<string>;
    reset(): Promise<void>;
    getAll(): Promise<Proxies>;
}
